            other['fliph'] = this.$applyBindRule("fliph", xmlBlock) == "true"
                ? true
                : false;
            other['rotation'] = parseInt(this.$applyBindRule("rotation", xmlBlock)) || 0;
            other['inputList'] = inputList;
