        this.$value = html;

        
        html = html.replace(/<p[^>]*>/gi, "").replace(/<\/p>/gi, 
            "<br _apf_marker='1' /><br _apf_marker='1' />");

        html = apf.htmlCleaner.prepare(html);
        

